package com.lutfullayevmuhammad.quran.core.di.module.networkimport com.lutfullayevmuhammad.quran.core.api.QuranApiimport dagger.Moduleimport dagger.Providesimport retrofit2.Retrofitimport javax.inject.Singleton@Moduleclass ApiModule {    @Provides    @Singleton    fun provideQuranApi(retrofit: Retrofit):  QuranApi{        return retrofit.create(QuranApi::class.java)    }}