package com.lutfullayevmuhammad.quran.core.di.ui.mainimport com.lutfullayevmuhammad.quran.ui.details.DetailsFragmentimport com.lutfullayevmuhammad.quran.ui.home.HomeFragmentimport dagger.Moduleimport dagger.android.ContributesAndroidInjectorimport com.lutfullayevmuhammad.quran.core.di.ui.main.mainFragment.DetailFragmentModuleimport com.lutfullayevmuhammad.quran.core.di.ui.main.mainFragment.HomeFragmentModule@Moduleinterface MainFragmentProviders {    @ContributesAndroidInjector(        modules = [            HomeFragmentModule::class        ]    )    fun provideHomeFragment(): HomeFragment    @ContributesAndroidInjector(        modules = [            DetailFragmentModule::class        ]    )    fun provideDetailsFragment(): DetailsFragment}